import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { ZoomIn, ExternalLink, X } from 'lucide-react';

const Gallery = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [galleryRef, galleryInView] = useInView({ threshold: 0.3, triggerOnce: true });

  const galleryItems = [
    {
      id: 1,
      title: 'Industrial Manufacturing',
      category: 'Production',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80',
      description: 'State-of-the-art manufacturing facility showcasing our production capabilities.'
    },
    {
      id: 2,
      title: 'Precision Engineering',
      category: 'Engineering',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80',
      description: 'Advanced engineering processes ensuring quality and precision in every component.'
    },
    {
      id: 3,
      title: 'Quality Control',
      category: 'Quality',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80',
      description: 'Rigorous quality control procedures maintaining our high standards.'
    },
    {
      id: 4,
      title: 'Research & Development',
      category: 'R&D',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80',
      description: 'Innovation center where cutting-edge technologies are developed and tested.'
    },
    {
      id: 5,
      title: 'Global Operations',
      category: 'Operations',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80',
      description: 'Worldwide network of facilities serving clients across the globe.'
    },
    {
      id: 6,
      title: 'Team Excellence',
      category: 'Team',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80',
      description: 'Our dedicated team of professionals committed to delivering excellence.'
    }
  ];

  const categories = ['All', 'Production', 'Engineering', 'Quality', 'R&D', 'Operations', 'Team'];

  return (
    <div className="min-h-screen pt-20">
      {/* Header */}
      <section className="section-padding bg-gradient-primary text-white">
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="text-center max-w-4xl mx-auto"
          >
            <h1 className="text-4xl md:text-6xl font-bold mb-6">
              Our <span className="text-accent-300">Gallery</span>
            </h1>
            <p className="text-xl md:text-2xl text-gray-200 leading-relaxed">
              Explore our facilities, processes, and the people behind our success
            </p>
          </motion.div>
        </div>
      </section>

      {/* Gallery Section */}
      <section 
        ref={galleryRef}
        className="section-padding bg-white dark:bg-secondary-900"
      >
        <div className="container-custom">
          {/* Categories Filter */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={galleryInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.6 }}
            className="flex flex-wrap justify-center gap-4 mb-12"
          >
            {categories.map((category) => (
              <button
                key={category}
                className="px-6 py-3 rounded-full bg-secondary-100 dark:bg-secondary-800 text-secondary-700 dark:text-secondary-300 hover:bg-primary-100 dark:hover:bg-primary-900/30 hover:text-primary-600 dark:hover:text-primary-400 transition-all duration-300 font-medium"
              >
                {category}
              </button>
            ))}
          </motion.div>

          {/* Gallery Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {galleryItems.map((item, index) => (
              <motion.div
                key={item.id}
                initial={{ opacity: 0, y: 50 }}
                animate={galleryInView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                whileHover={{ 
                  y: -10,
                  rotateY: 5,
                  rotateX: 5
                }}
                className="group cursor-pointer perspective-1000"
                onClick={() => setSelectedImage(item)}
              >
                <div className="card overflow-hidden transform transition-all duration-500 group-hover:shadow-2xl">
                  <div className="relative overflow-hidden">
                    <img
                      src={item.image}
                      alt={item.title}
                      className="w-full h-64 object-cover group-hover:scale-110 transition-transform duration-700"
                    />

                    {/* Overlay */}
                    <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent opacity-0 group-hover:opacity-100 transition-all duration-500">
                      <div className="absolute inset-0 flex items-center justify-center">
                        <div className="text-center text-white transform translate-y-4 group-hover:translate-y-0 transition-transform duration-500">
                          <ZoomIn size={48} className="mx-auto mb-4 opacity-80" />
                          <h3 className="text-xl font-semibold mb-2">{item.title}</h3>
                          <p className="text-sm opacity-90">{item.description}</p>
                        </div>
                      </div>
                    </div>

                    {/* Category Badge */}
                    <div className="absolute top-4 left-4">
                      <span className="px-3 py-1 bg-white/90 dark:bg-secondary-800/90 text-secondary-700 dark:text-secondary-300 rounded-full text-sm font-medium">
                        {item.category}
                      </span>
                    </div>
                  </div>

                  <div className="p-6">
                    <h3 className="text-xl font-semibold mb-2 text-secondary-900 dark:text-white group-hover:text-primary-600 dark:group-hover:text-primary-400 transition-colors">
                      {item.title}
                    </h3>
                    <p className="text-secondary-600 dark:text-secondary-300 mb-4">
                      {item.description}
                    </p>
                    <button className="btn-primary text-sm">
                      <ExternalLink size={16} className="mr-2" />
                      View Details
                    </button>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Image Modal */}
      {selectedImage && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-4"
          onClick={() => setSelectedImage(null)}
        >
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.9, opacity: 0 }}
            className="relative max-w-4xl w-full"
            onClick={(e) => e.stopPropagation()}
          >
            <button
              onClick={() => setSelectedImage(null)}
              className="absolute -top-12 right-0 text-white hover:text-gray-300 transition-colors"
            >
              <X size={32} />
            </button>

            <div className="bg-white dark:bg-secondary-800 rounded-2xl overflow-hidden">
              <img
                src={selectedImage.image}
                alt={selectedImage.title}
                className="w-full h-96 object-cover"
              />
              <div className="p-8">
                <div className="flex items-center justify-between mb-4">
                  <span className="px-3 py-1 bg-primary-100 dark:bg-primary-900/30 text-primary-600 dark:text-primary-400 rounded-full text-sm font-medium">
                    {selectedImage.category}
                  </span>
                </div>
                <h2 className="text-3xl font-bold mb-4 text-secondary-900 dark:text-white">
                  {selectedImage.title}
                </h2>
                <p className="text-lg text-secondary-600 dark:text-secondary-300">
                  {selectedImage.description}
                </p>
              </div>
            </div>
          </motion.div>
        </motion.div>
      )}
    </div>
  );
};

export default Gallery;
