import React from 'react';
import { motion } from 'framer-motion';
import { ChevronDown, Wrench, Settings, Truck, Shield, Users, Award } from 'lucide-react';
import { useInView } from 'react-intersection-observer';

const Home = () => {
  const [heroRef, heroInView] = useInView({ threshold: 0.3, triggerOnce: true });
  const [introRef, introInView] = useInView({ threshold: 0.3, triggerOnce: true });
  const [servicesRef, servicesInView] = useInView({ threshold: 0.3, triggerOnce: true });

  const services = [
    {
      icon: Wrench,
      title: 'Custom Manufacturing',
      description: 'Precision engineering and custom hardware solutions tailored to your specific needs.',
      color: 'from-blue-500 to-cyan-500'
    },
    {
      icon: Settings,
      title: 'Industrial Automation',
      description: 'Advanced automation systems to optimize your production processes.',
      color: 'from-purple-500 to-pink-500'
    },
    {
      icon: Truck,
      title: 'Logistics Solutions',
      description: 'Comprehensive supply chain and logistics management services.',
      color: 'from-green-500 to-emerald-500'
    },
    {
      icon: Shield,
      title: 'Quality Assurance',
      description: 'Rigorous testing and quality control for all our products.',
      color: 'from-red-500 to-orange-500'
    },
    {
      icon: Users,
      title: 'Expert Support',
      description: '24/7 technical support and maintenance services.',
      color: 'from-indigo-500 to-blue-500'
    },
    {
      icon: Award,
      title: 'Industry Leader',
      description: 'Over 25 years of experience in industrial hardware solutions.',
      color: 'from-yellow-500 to-orange-500'
    }
  ];

  const scrollToContent = () => {
    document.getElementById('intro-section').scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section 
        ref={heroRef}
        className="relative min-h-screen flex items-center justify-center overflow-hidden"
        style={{
          backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80')`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundAttachment: 'fixed'
        }}
      >
        <div className="container-custom text-center text-white z-10">
          <motion.h1
            initial={{ opacity: 0, y: 50 }}
            animate={heroInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8 }}
            className="text-4xl md:text-6xl lg:text-7xl font-bold mb-6"
          >
            <span className="text-gradient">TechHardware</span> Pro
          </motion.h1>
          
          <motion.p
            initial={{ opacity: 0, y: 30 }}
            animate={heroInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.2 }}
            className="text-xl md:text-2xl mb-8 max-w-3xl mx-auto text-gray-200"
          >
            Leading the future of industrial hardware solutions with innovative technology and unmatched expertise
          </motion.p>
          
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={heroInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.4 }}
            className="flex flex-col sm:flex-row gap-4 justify-center"
          >
            <button className="btn-primary text-lg px-8 py-4">
              Explore Products
            </button>
            <button className="btn-secondary text-lg px-8 py-4">
              Get Quote
            </button>
          </motion.div>
        </div>

        {/* Scroll Indicator */}
        <motion.button
          onClick={scrollToContent}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1 }}
          className="absolute bottom-8 left-1/2 transform -translate-x-1/2 text-white animate-bounce-slow"
        >
          <ChevronDown size={32} />
        </motion.button>
      </section>

      {/* Intro Section */}
      <section 
        id="intro-section"
        ref={introRef}
        className="section-padding bg-white dark:bg-secondary-900"
      >
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={introInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8 }}
            className="max-w-4xl mx-auto text-center"
          >
            <h2 className="text-3xl md:text-4xl font-bold mb-6 text-secondary-900 dark:text-white">
              Building Tomorrow's Infrastructure Today
            </h2>
            <p className="text-lg text-secondary-600 dark:text-secondary-300 leading-relaxed">
              At TechHardware Pro, we've been at the forefront of industrial innovation for over two decades. 
              Our commitment to excellence, cutting-edge technology, and customer satisfaction has made us the 
              trusted partner for businesses worldwide. From precision manufacturing to advanced automation, 
              we deliver solutions that drive success and efficiency in every industry.
            </p>
          </motion.div>
        </div>
      </section>

      {/* Services Section */}
      <section 
        ref={servicesRef}
        className="section-padding bg-secondary-50 dark:bg-secondary-800"
      >
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={servicesInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8 }}
            className="text-center mb-16"
          >
            <h2 className="text-3xl md:text-4xl font-bold mb-6 text-secondary-900 dark:text-white">
              Our Core Services
            </h2>
            <p className="text-lg text-secondary-600 dark:text-secondary-300 max-w-2xl mx-auto">
              Comprehensive hardware solutions designed to meet the evolving needs of modern industry
            </p>
          </motion.div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {services.map((service, index) => (
              <motion.div
                key={service.title}
                initial={{ opacity: 0, y: 50 }}
                animate={servicesInView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                whileHover={{ y: -10 }}
                className="card p-8 text-center group"
              >
                <div className={`w-16 h-16 bg-gradient-to-br ${service.color} rounded-xl flex items-center justify-center mx-auto mb-6 group-hover:scale-110 transition-transform duration-300`}>
                  <service.icon size={32} className="text-white" />
                </div>
                <h3 className="text-xl font-semibold mb-4 text-secondary-900 dark:text-white">
                  {service.title}
                </h3>
                <p className="text-secondary-600 dark:text-secondary-300">
                  {service.description}
                </p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
