import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { Award, Users, Globe, Target, TrendingUp, CheckCircle, Shield, Clock, Truck, Headphones, Leaf, Zap } from 'lucide-react';

const About = () => {
  const [headerRef, headerInView] = useInView({ threshold: 0.3, triggerOnce: true });
  const [companyRef, companyInView] = useInView({ threshold: 0.3, triggerOnce: true });
  const [whyChooseRef, whyChooseInView] = useInView({ threshold: 0.3, triggerOnce: true });
  const [missionRef, missionInView] = useInView({ threshold: 0.3, triggerOnce: true });

  const whyChooseUs = [
    {
      icon: Clock,
      title: 'Over a decade of expertise',
      description: 'in earthing solutions',
      color: 'from-blue-500 to-cyan-500'
    },
    {
      icon: Shield,
      title: 'High-quality, durable',
      description: 'and corrosion-resistant products',
      color: 'from-green-500 to-emerald-500'
    },
    {
      icon: Award,
      title: 'Advanced manufacturing',
      description: 'with strict quality control',
      color: 'from-purple-500 to-pink-500'
    },
    {
      icon: TrendingUp,
      title: 'Competitive pricing',
      description: 'with cost-effective solutions',
      color: 'from-yellow-500 to-orange-500'
    },
    {
      icon: Target,
      title: 'Customization',
      description: 'to meet specific project needs',
      color: 'from-red-500 to-pink-500'
    },
    {
      icon: Truck,
      title: 'Timely delivery',
      description: 'with a strong distribution network',
      color: 'from-indigo-500 to-blue-500'
    },
    {
      icon: Headphones,
      title: 'Dedicated customer support',
      description: 'and technical assistance',
      color: 'from-teal-500 to-cyan-500'
    },
    {
      icon: Leaf,
      title: 'Commitment to eco-friendly',
      description: 'and sustainable practices',
      color: 'from-green-500 to-teal-500'
    }
  ];

  return (
    <div className="min-h-screen pt-20">
      {/* Header Section */}
      <section 
        ref={headerRef}
        className="section-padding bg-gradient-primary text-white"
      >
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={headerInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8 }}
            className="text-center max-w-4xl mx-auto"
          >
            <h1 className="text-4xl md:text-6xl font-bold mb-6">
              About <span className="text-accent-300">TechHardware</span> Pro
            </h1>
            <p className="text-xl md:text-2xl text-gray-200 leading-relaxed">
              Pioneering the future of industrial hardware with innovation, quality, and unwavering commitment to excellence.
            </p>
          </motion.div>
        </div>
      </section>

      {/* Company Story */}
      <section className="section-padding bg-white dark:bg-secondary-900">
        <div className="container-custom">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            <motion.div
              initial={{ opacity: 0, x: -50 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
            >
              <h2 className="text-3xl md:text-4xl font-bold mb-6 text-secondary-900 dark:text-white">
                Our Story
              </h2>
              <p className="text-lg text-secondary-600 dark:text-secondary-300 mb-6 leading-relaxed">
                Founded in 1998, TechHardware Pro began as a small workshop with a big dream: to revolutionize 
                the industrial hardware industry. What started with a handful of passionate engineers has grown 
                into a global enterprise serving clients across 50+ countries.
              </p>
              <p className="text-lg text-secondary-600 dark:text-secondary-300 mb-6 leading-relaxed">
                Today, we're proud to be at the forefront of technological innovation, delivering cutting-edge 
                solutions that drive efficiency, sustainability, and growth for businesses worldwide. Our 
                commitment to quality, innovation, and customer satisfaction remains the cornerstone of everything we do.
              </p>
              <div className="flex flex-wrap gap-4">
                <div className="flex items-center space-x-2">
                  <CheckCircle className="text-green-500" size={20} />
                  <span className="text-secondary-700 dark:text-secondary-300">ISO 9001 Certified</span>
                </div>
                <div className="flex items-center space-x-2">
                  <CheckCircle className="text-green-500" size={20} />
                  <span className="text-secondary-700 dark:text-secondary-300">25+ Years Experience</span>
                </div>
                <div className="flex items-center space-x-2">
                  <CheckCircle className="text-green-500" size={20} />
                  <span className="text-secondary-700 dark:text-secondary-300">Global Reach</span>
                </div>
              </div>
            </motion.div>
            
            <motion.div
              initial={{ opacity: 0, x: 50 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
              className="relative"
            >
              <div className="relative z-10">
                <img 
                  src="https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80" 
                  alt="Industrial Facility" 
                  className="rounded-2xl shadow-2xl"
                />
              </div>
              <div className="absolute -top-4 -right-4 w-32 h-32 bg-gradient-to-br from-accent-400 to-accent-600 rounded-full opacity-20 animate-float"></div>
              <div className="absolute -bottom-4 -left-4 w-24 h-24 bg-gradient-to-br from-primary-400 to-primary-600 rounded-full opacity-20 animate-float" style={{ animationDelay: '1s' }}></div>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section 
        ref={statsRef}
        className="section-padding bg-secondary-50 dark:bg-secondary-800"
      >
        <div className="container-custom">
          <div className="grid grid-cols-2 md:grid-cols-4 gap-8">
            {stats.map((stat, index) => (
              <motion.div
                key={stat.label}
                initial={{ opacity: 0, y: 50 }}
                animate={statsInView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                className="text-center"
              >
                <div className={`w-16 h-16 bg-gradient-to-br ${stat.color} rounded-xl flex items-center justify-center mx-auto mb-4`}>
                  <stat.icon size={32} className="text-white" />
                </div>
                <div className="text-3xl md:text-4xl font-bold text-secondary-900 dark:text-white mb-2">
                  {stat.number}
                </div>
                <div className="text-secondary-600 dark:text-secondary-300">
                  {stat.label}
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Timeline Section */}
      <section 
        ref={timelineRef}
        className="section-padding bg-white dark:bg-secondary-900"
      >
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={timelineInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8 }}
            className="text-center mb-16"
          >
            <h2 className="text-3xl md:text-4xl font-bold mb-6 text-secondary-900 dark:text-white">
              Our Journey
            </h2>
            <p className="text-lg text-secondary-600 dark:text-secondary-300 max-w-2xl mx-auto">
              A timeline of milestones that shaped our company's growth and success
            </p>
          </motion.div>

          <div className="relative">
            {/* Timeline Line */}
            <div className="absolute left-1/2 transform -translate-x-1/2 w-1 h-full bg-gradient-to-b from-primary-500 to-accent-500"></div>
            
            <div className="space-y-12">
              {timeline.map((item, index) => (
                <motion.div
                  key={item.year}
                  initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
                  animate={timelineInView ? { opacity: 1, x: 0 } : {}}
                  transition={{ duration: 0.6, delay: index * 0.2 }}
                  className={`flex items-center ${index % 2 === 0 ? 'flex-row' : 'flex-row-reverse'}`}
                >
                  <div className={`w-1/2 ${index % 2 === 0 ? 'pr-8 text-right' : 'pl-8 text-left'}`}>
                    <div className="card p-6">
                      <div className="text-3xl mb-2">{item.icon}</div>
                      <h3 className="text-xl font-semibold mb-2 text-secondary-900 dark:text-white">
                        {item.title}
                      </h3>
                      <p className="text-secondary-600 dark:text-secondary-300">
                        {item.description}
                      </p>
                    </div>
                  </div>
                  
                  <div className="relative z-10">
                    <div className="w-4 h-4 bg-primary-500 rounded-full border-4 border-white dark:border-secondary-900 shadow-lg"></div>
                  </div>
                  
                  <div className={`w-1/2 ${index % 2 === 0 ? 'pl-8' : 'pr-8'}`}>
                    <div className="text-2xl font-bold text-primary-600 dark:text-primary-400">
                      {item.year}
                    </div>
                  </div>
                </motion.div>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Values Section */}
      <section className="section-padding bg-secondary-50 dark:bg-secondary-800">
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="text-center mb-16"
          >
            <h2 className="text-3xl md:text-4xl font-bold mb-6 text-secondary-900 dark:text-white">
              Our Values
            </h2>
            <p className="text-lg text-secondary-600 dark:text-secondary-300 max-w-2xl mx-auto">
              The principles that guide our decisions and shape our culture
            </p>
          </motion.div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {values.map((value, index) => (
              <motion.div
                key={value.title}
                initial={{ opacity: 0, y: 50 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.2 }}
                viewport={{ once: true }}
                whileHover={{ y: -10 }}
                className="card p-8 text-center"
              >
                <div className="w-16 h-16 bg-gradient-to-br from-primary-500 to-accent-500 rounded-xl flex items-center justify-center mx-auto mb-6">
                  <value.icon size={32} className="text-white" />
                </div>
                <h3 className="text-xl font-semibold mb-4 text-secondary-900 dark:text-white">
                  {value.title}
                </h3>
                <p className="text-secondary-600 dark:text-secondary-300">
                  {value.description}
                </p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default About;
