import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { X, ExternalLink, Star, ShoppingCart } from 'lucide-react';

const Products = () => {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [productsRef, productsInView] = useInView({ threshold: 0.3, triggerOnce: true });

  const products = [
    {
      id: 1,
      name: 'Industrial Drill Press',
      category: 'Machinery',
      price: '$2,499',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80',
      description: 'High-precision industrial drill press with advanced safety features.',
      rating: 4.8,
      reviews: 124,
      inStock: true
    },
    {
      id: 2,
      name: 'CNC Milling Machine',
      category: 'Automation',
      price: '$15,999',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80',
      description: 'Computer-controlled milling machine for precise manufacturing.',
      rating: 4.9,
      reviews: 89,
      inStock: true
    },
    {
      id: 3,
      name: 'Hydraulic Press',
      category: 'Machinery',
      price: '$8,750',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80',
      description: 'Heavy-duty hydraulic press for industrial forming operations.',
      rating: 4.7,
      reviews: 156,
      inStock: false
    }
  ];

  return (
    <div className="min-h-screen pt-20">
      {/* Header */}
      <section className="section-padding bg-gradient-primary text-white">
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="text-center max-w-4xl mx-auto"
          >
            <h1 className="text-4xl md:text-6xl font-bold mb-6">
              Our <span className="text-accent-300">Products</span>
            </h1>
            <p className="text-xl md:text-2xl text-gray-200 leading-relaxed">
              Discover our comprehensive range of industrial hardware solutions
            </p>
          </motion.div>
        </div>
      </section>

      {/* Products Grid */}
      <section 
        ref={productsRef}
        className="section-padding bg-white dark:bg-secondary-900"
      >
        <div className="container-custom">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {products.map((product, index) => (
              <motion.div
                key={product.id}
                initial={{ opacity: 0, y: 50 }}
                animate={productsInView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                whileHover={{ y: -10 }}
                className="group cursor-pointer"
                onClick={() => setSelectedProduct(product)}
              >
                <div className="card overflow-hidden">
                  <div className="relative overflow-hidden">
                    <img
                      src={product.image}
                      alt={product.name}
                      className="w-full h-64 object-cover group-hover:scale-110 transition-transform duration-500"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>

                    <div className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                      <div className="text-center text-white">
                        <button className="btn-primary mb-4">
                          View Details
                        </button>
                      </div>
                    </div>

                    <div className="absolute top-4 left-4">
                      <span className="px-3 py-1 bg-white/90 dark:bg-secondary-800/90 text-secondary-700 dark:text-secondary-300 rounded-full text-sm font-medium">
                        {product.category}
                      </span>
                    </div>

                    <div className="absolute top-4 right-4">
                      <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                        product.inStock 
                          ? 'bg-green-500/90 text-white' 
                          : 'bg-red-500/90 text-white'
                      }`}>
                        {product.inStock ? 'In Stock' : 'Out of Stock'}
                      </span>
                    </div>
                  </div>

                  <div className="p-6">
                    <h3 className="text-xl font-semibold mb-2 text-secondary-900 dark:text-white group-hover:text-primary-600 dark:group-hover:text-primary-400 transition-colors">
                      {product.name}
                    </h3>

                    <div className="flex items-center mb-3">
                      <div className="flex items-center">
                        {[...Array(5)].map((_, i) => (
                          <Star
                            key={i}
                            size={16}
                            className={i < Math.floor(product.rating) ? 'text-yellow-400 fill-current' : 'text-gray-300'}
                          />
                        ))}
                      </div>
                      <span className="ml-2 text-sm text-secondary-600 dark:text-secondary-300">
                        {product.rating} ({product.reviews})
                      </span>
                    </div>

                    <p className="text-secondary-600 dark:text-secondary-300 mb-4">
                      {product.description}
                    </p>

                    <div className="flex items-center justify-between">
                      <div className="text-2xl font-bold text-primary-600 dark:text-primary-400">
                        {product.price}
                      </div>
                      <button className="btn-primary text-sm">
                        <ShoppingCart size={16} className="mr-2" />
                        Add to Cart
                      </button>
                    </div>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Products;
