import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { Star, Quote, ChevronLeft, ChevronRight, Building, Users, Award } from 'lucide-react';

const Clients = () => {
  const [currentTestimonial, setCurrentTestimonial] = useState(0);
  const [clientsRef, clientsInView] = useInView({ threshold: 0.3, triggerOnce: true });

  const clients = [
    {
      id: 1,
      name: 'John Smith',
      position: 'CEO',
      company: 'Industrial Solutions Inc.',
      logo: '🏭',
      rating: 5,
      testimonial: 'TechHardware Pro has been instrumental in transforming our manufacturing processes. Their innovative solutions and exceptional service have exceeded our expectations.',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80'
    },
    {
      id: 2,
      name: 'Sarah Johnson',
      position: 'Operations Manager',
      company: 'Global Manufacturing Co.',
      logo: '🏢',
      rating: 5,
      testimonial: 'The quality and reliability of TechHardware Pro\'s products are outstanding. Their team\'s expertise and commitment to excellence make them our trusted partner.',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80'
    },
    {
      id: 3,
      name: 'Michael Chen',
      position: 'Engineering Director',
      company: 'Precision Engineering Ltd.',
      logo: '⚙️',
      rating: 5,
      testimonial: 'Working with TechHardware Pro has been a game-changer for our engineering projects. Their technical expertise and customer support are unmatched.',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80'
    },
    {
      id: 4,
      name: 'Emily Rodriguez',
      position: 'Procurement Manager',
      company: 'Advanced Systems Corp.',
      logo: '🔧',
      rating: 5,
      testimonial: 'TechHardware Pro consistently delivers high-quality products on time. Their attention to detail and professional service make them our preferred supplier.',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80'
    },
    {
      id: 5,
      name: 'David Wilson',
      position: 'Plant Manager',
      company: 'SteelWorks Industries',
      logo: '🏗️',
      rating: 5,
      testimonial: 'The automation solutions from TechHardware Pro have significantly improved our production efficiency. Their ongoing support and maintenance are exceptional.',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80'
    },
    {
      id: 6,
      name: 'Lisa Thompson',
      position: 'Quality Assurance Lead',
      company: 'Aerospace Components',
      logo: '✈️',
      rating: 5,
      testimonial: 'TechHardware Pro\'s commitment to quality and precision aligns perfectly with our aerospace standards. They are a reliable partner for critical applications.',
      image: 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80'
    }
  ];

  const stats = [
    { icon: Building, number: '500+', label: 'Clients Worldwide', color: 'from-blue-500 to-cyan-500' },
    { icon: Users, number: '50+', label: 'Countries Served', color: 'from-green-500 to-emerald-500' },
    { icon: Award, number: '98%', label: 'Client Satisfaction', color: 'from-yellow-500 to-orange-500' }
  ];

  const nextTestimonial = () => {
    setCurrentTestimonial((prev) => (prev + 1) % clients.length);
  };

  const prevTestimonial = () => {
    setCurrentTestimonial((prev) => (prev - 1 + clients.length) % clients.length);
  };

  return (
    <div className="min-h-screen pt-20">
      {/* Header */}
      <section className="section-padding bg-gradient-primary text-white">
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="text-center max-w-4xl mx-auto"
          >
            <h1 className="text-4xl md:text-6xl font-bold mb-6">
              Our <span className="text-accent-300">Clients</span>
            </h1>
            <p className="text-xl md:text-2xl text-gray-200 leading-relaxed">
              Trusted by leading companies worldwide for innovative hardware solutions
            </p>
          </motion.div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="section-padding bg-white dark:bg-secondary-900">
        <div className="container-custom">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {stats.map((stat, index) => (
              <motion.div
                key={stat.label}
                initial={{ opacity: 0, y: 50 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                className="text-center"
              >
                <div className={`w-16 h-16 bg-gradient-to-br ${stat.color} rounded-xl flex items-center justify-center mx-auto mb-4`}>
                  <stat.icon size={32} className="text-white" />
                </div>
                <div className="text-3xl md:text-4xl font-bold text-secondary-900 dark:text-white mb-2">
                  {stat.number}
                </div>
                <div className="text-secondary-600 dark:text-secondary-300">
                  {stat.label}
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Testimonials Section */}
      <section 
        ref={clientsRef}
        className="section-padding bg-secondary-50 dark:bg-secondary-800"
      >
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            animate={clientsInView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8 }}
            className="text-center mb-16"
          >
            <h2 className="text-3xl md:text-4xl font-bold mb-6 text-secondary-900 dark:text-white">
              What Our Clients Say
            </h2>
            <p className="text-lg text-secondary-600 dark:text-secondary-300 max-w-2xl mx-auto">
              Hear from our satisfied clients about their experience working with TechHardware Pro
            </p>
          </motion.div>

          {/* Testimonial Carousel */}
          <div className="relative max-w-4xl mx-auto">
            <motion.div
              key={currentTestimonial}
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -50 }}
              transition={{ duration: 0.5 }}
              className="card p-8 md:p-12"
            >
              <div className="flex flex-col md:flex-row items-center md:items-start gap-8">
                <div className="flex-shrink-0">
                  <div className="w-24 h-24 rounded-full overflow-hidden border-4 border-primary-100 dark:border-primary-900/30">
                    <img
                      src={clients[currentTestimonial].image}
                      alt={clients[currentTestimonial].name}
                      className="w-full h-full object-cover"
                    />
                  </div>
                </div>
                
                <div className="flex-1 text-center md:text-left">
                  <div className="flex justify-center md:justify-start mb-4">
                    {[...Array(clients[currentTestimonial].rating)].map((_, i) => (
                      <Star key={i} size={20} className="text-yellow-400 fill-current" />
                    ))}
                  </div>
                  
                  <blockquote className="text-lg md:text-xl text-secondary-700 dark:text-secondary-300 mb-6 leading-relaxed">
                    "{clients[currentTestimonial].testimonial}"
                  </blockquote>
                  
                  <div className="flex items-center justify-center md:justify-start space-x-4">
                    <div className="text-3xl">{clients[currentTestimonial].logo}</div>
                    <div>
                      <div className="font-semibold text-secondary-900 dark:text-white">
                        {clients[currentTestimonial].name}
                      </div>
                      <div className="text-sm text-secondary-600 dark:text-secondary-300">
                        {clients[currentTestimonial].position}, {clients[currentTestimonial].company}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </motion.div>

            {/* Navigation Buttons */}
            <button
              onClick={prevTestimonial}
              className="absolute left-4 top-1/2 transform -translate-y-1/2 w-12 h-12 bg-white dark:bg-secondary-800 rounded-full shadow-lg flex items-center justify-center hover:bg-secondary-50 dark:hover:bg-secondary-700 transition-colors"
            >
              <ChevronLeft size={24} className="text-secondary-600 dark:text-secondary-300" />
            </button>
            
            <button
              onClick={nextTestimonial}
              className="absolute right-4 top-1/2 transform -translate-y-1/2 w-12 h-12 bg-white dark:bg-secondary-800 rounded-full shadow-lg flex items-center justify-center hover:bg-secondary-50 dark:hover:bg-secondary-700 transition-colors"
            >
              <ChevronRight size={24} className="text-secondary-600 dark:text-secondary-300" />
            </button>

            {/* Dots Indicator */}
            <div className="flex justify-center mt-8 space-x-2">
              {clients.map((_, index) => (
                <button
                  key={index}
                  onClick={() => setCurrentTestimonial(index)}
                  className={`w-3 h-3 rounded-full transition-colors ${
                    index === currentTestimonial
                      ? 'bg-primary-600 dark:bg-primary-400'
                      : 'bg-secondary-300 dark:bg-secondary-600'
                  }`}
                />
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Client Grid */}
      <section className="section-padding bg-white dark:bg-secondary-900">
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="text-center mb-16"
          >
            <h2 className="text-3xl md:text-4xl font-bold mb-6 text-secondary-900 dark:text-white">
              Trusted by Industry Leaders
            </h2>
            <p className="text-lg text-secondary-600 dark:text-secondary-300 max-w-2xl mx-auto">
              We serve clients across various industries, from manufacturing to aerospace
            </p>
          </motion.div>

          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-8">
            {clients.map((client, index) => (
              <motion.div
                key={client.id}
                initial={{ opacity: 0, scale: 0.8 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                whileHover={{ scale: 1.05 }}
                className="card p-6 text-center group"
              >
                <div className="text-4xl mb-4 group-hover:scale-110 transition-transform duration-300">
                  {client.logo}
                </div>
                <h3 className="font-semibold text-secondary-900 dark:text-white mb-2">
                  {client.company}
                </h3>
                <div className="flex justify-center">
                  {[...Array(client.rating)].map((_, i) => (
                    <Star key={i} size={16} className="text-yellow-400 fill-current" />
                  ))}
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="section-padding bg-gradient-primary text-white">
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="text-center max-w-3xl mx-auto"
          >
            <h2 className="text-3xl md:text-4xl font-bold mb-6">
              Ready to Join Our Success Stories?
            </h2>
            <p className="text-xl text-gray-200 mb-8 leading-relaxed">
              Partner with TechHardware Pro and experience the difference that quality, innovation, and exceptional service can make for your business.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <button className="btn-accent text-lg px-8 py-4">
                Get Started Today
              </button>
              <button className="btn-secondary text-lg px-8 py-4">
                Request Quote
              </button>
            </div>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default Clients;
