import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { X, ExternalLink, Star, Zap, Shield, Award, CheckCircle } from 'lucide-react';

const Products = () => {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [productsRef, productsInView] = useInView({ threshold: 0.3, triggerOnce: true });

  const products = [
    {
      id: 1,
      name: 'NE GI Earthing Electrode (Pipe in Strip)',
      category: 'Earthing Solutions',
      price: 'Contact Us',
      image: '/products/product1.png',
      description: 'Hot dip galvanized earthing electrode with superior corrosion protection and fast fault current dissipation.',
      rating: 4.9,
      reviews: 156,
      inStock: true,
      icon: Shield,
      color: 'from-blue-500 to-cyan-500',
      specifications: [
        { model: '48 MNEPG', length: '1,2,3 & 6', terminal: '40x6', innerStrip: '25x3', diameter: '48' },
        { model: '58 MNEPG', length: '1,2,3 & 6', terminal: '50x6', innerStrip: '25x3', diameter: '58' },
        { model: '76 MNEPG', length: '1,2,3 & 6', terminal: '50x6', innerStrip: '25x3', diameter: '76' },
        { model: '88 MNEPG', length: '1,2,3 & 6', terminal: '50x6', innerStrip: '25x3', diameter: '88' }
      ],
      features: [
        '✅ Hot dip galvanized for corrosion protection',
        '⚡ Designed for fast fault current dissipation',
        '🔧 Low maintenance on site',
        '⏳ Easy & fast installation on site',
        '🌍 Suitable for soil with pH 5.0 to 8.0',
        '💧 Moisture booster chemical bags provided for low earth resistance'
      ]
    },
    {
      id: 2,
      name: 'NE Copper Terminal Earthing Electrode',
      category: 'Earthing Solutions',
      price: 'Contact Us',
      image: '/products/product2.png',
      description: 'Copper terminal earthing electrode with enhanced conductivity and durability.',
      rating: 4.8,
      reviews: 142,
      inStock: true,
      icon: Zap,
      color: 'from-yellow-500 to-orange-500',
      specifications: [
        { model: '48 MNECT', length: '1,2,3 & 6', terminal: '40x6', innerStrip: '25x3', diameter: '48' },
        { model: '58 MNECT', length: '1,2,3 & 6', terminal: '50x6', innerStrip: '25x3', diameter: '58' },
        { model: '76 MNECT', length: '1,2,3 & 6', terminal: '50x6', innerStrip: '25x3', diameter: '76' },
        { model: '88 MNECT', length: '1,2,3 & 6', terminal: '50x6', innerStrip: '25x3', diameter: '88' }
      ],
      features: [
        '✅ Hot dip galvanized for corrosion protection',
        '⚡ Designed for fast fault current dissipation',
        '🔧 Low maintenance on site',
        '⏳ Easy & fast installation on site',
        '🌍 Suitable for soil with pH 5.0 to 8.0',
        '💧 Moisture booster chemical bags provided for low earth resistance'
      ]
    }
  ];

  return (
    <div className="min-h-screen pt-20 bg-white dark:bg-secondary-900">
      {/* Header */}
      <section className="py-16 bg-gradient-to-br from-primary-600 via-primary-700 to-secondary-800 text-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="text-center max-w-4xl mx-auto"
          >
            <h1 className="text-4xl md:text-6xl font-bold mb-6">
              Our <span className="text-accent-300">Products</span>
            </h1>
            <p className="text-xl md:text-2xl text-gray-200 leading-relaxed">
              Discover our comprehensive range of industrial hardware solutions
            </p>
          </motion.div>
        </div>
      </section>

      {/* Products Grid */}
      <section className="py-16 bg-white dark:bg-secondary-900">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="space-y-16">
            {products.map((product, index) => (
              <motion.div
                key={product.id}
                initial={{ opacity: 0, y: 50 }}
                animate={productsInView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.8, delay: index * 0.2 }}
                whileHover={{ y: -5 }}
                className="bg-white dark:bg-secondary-800 rounded-xl shadow-lg hover:shadow-2xl transition-all duration-500 border border-gray-200 dark:border-secondary-700 overflow-hidden"
              >
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-0">
                  {/* Product Image */}
                  <motion.div
                    initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
                    animate={productsInView ? { opacity: 1, x: 0 } : {}}
                    transition={{ duration: 0.8, delay: index * 0.2 + 0.2 }}
                    className="relative overflow-hidden cursor-pointer"
                    onClick={() => setSelectedProduct(product)}
                  >
                    <div className="relative h-96 lg:h-full">
                      <img
                        src={product.image}
                        alt={product.name}
                        className="w-full h-full object-cover hover:scale-105 transition-transform duration-700"
                      />
                      <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-0 hover:opacity-100 transition-opacity duration-300"></div>

                      <div className="absolute top-6 left-6">
                        <span className="px-4 py-2 bg-white/90 dark:bg-secondary-800/90 text-secondary-700 dark:text-secondary-300 rounded-full text-sm font-medium backdrop-blur-sm">
                          {product.category}
                        </span>
                      </div>

                      <div className="absolute top-6 right-6">
                        <span className={`px-4 py-2 rounded-full text-sm font-medium backdrop-blur-sm ${
                          product.inStock 
                            ? 'bg-green-500/90 text-white' 
                            : 'bg-red-500/90 text-white'
                        }`}>
                          {product.inStock ? 'In Stock' : 'Out of Stock'}
                        </span>
                      </div>
                    </div>
                  </motion.div>

                  {/* Product Content */}
                  <motion.div
                    initial={{ opacity: 0, x: index % 2 === 0 ? 50 : -50 }}
                    animate={productsInView ? { opacity: 1, x: 0 } : {}}
                    transition={{ duration: 0.8, delay: index * 0.2 + 0.4 }}
                    className="p-8 lg:p-12 flex flex-col justify-center"
                  >
                    <div className="mb-6">
                      <div className={`w-12 h-12 bg-gradient-to-br ${product.color} rounded-lg flex items-center justify-center mb-4 hover:scale-110 transition-transform duration-300 shadow-lg`}>
                        <product.icon size={24} className="text-white" />
                      </div>
                      <span className="px-4 py-2 bg-primary-100 dark:bg-primary-900 text-primary-700 dark:text-primary-300 rounded-full text-sm font-medium">
                        {product.category}
                      </span>
                    </div>

                    <h2 className="text-2xl md:text-3xl font-bold mb-4 text-secondary-900 dark:text-white hover:text-primary-600 dark:hover:text-primary-400 transition-colors duration-300">
                      {product.name}
                    </h2>

                    <div className="flex items-center mb-4">
                      <div className="flex items-center">
                        {[...Array(5)].map((_, i) => (
                          <Star
                            key={i}
                            size={18}
                            className={i < Math.floor(product.rating) ? 'text-yellow-400 fill-current' : 'text-gray-300'}
                          />
                        ))}
                      </div>
                      <span className="ml-3 text-secondary-600 dark:text-secondary-300 text-sm">
                        {product.rating} ({product.reviews} reviews)
                      </span>
                    </div>

                    <p className="text-lg text-secondary-600 dark:text-secondary-300 mb-6 leading-relaxed">
                      {product.description}
                    </p>

                    <div className="flex items-center justify-between pt-6 border-t border-gray-200 dark:border-secondary-700">
                      <div className="text-2xl font-bold text-primary-600 dark:text-primary-400">
                        {product.price}
                      </div>
                      <button 
                        className="bg-primary-600 hover:bg-primary-700 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl"
                        onClick={() => setSelectedProduct(product)}
                      >
                        <ExternalLink size={16} className="mr-2 inline" />
                        View Full Details
                      </button>
                    </div>
                  </motion.div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Products;
